import React, { Component } from "react";
import Card from "./components/Cards/Card";
import Wrapper from "./components/Wrapper/Wrapper";
import Score from "./components/Score/Score";
import images from "./images.json";
import  "./app.css";


class App extends Component {
  // Setting this.state.images to the cards json array
  state = {
    images,
    clickedImgIds: [],
    score: 0,
    goal: 8,
    status: ""
  };

  //shuffle the cards in the browser when clicked
  shuffleImg = id => {
    let clickedImgIds = this.state.clickedImgIds;

    if(clickedImgIds.includes(id)){
      this.setState({ clickedImgIds: [], score: 0, status:  "Game Over!" });
      return;
    }else{
      clickedImgIds.push(id)

      if(clickedImgIds.length === 8){
        this.setState({score: 8, status: "You Won!", clickedImgIds: []});
        console.log('You Win');
        return;
      }

      this.setState({ images, clickedImgIds, score: clickedImgIds.length, status: " " });

      for (let i = images.length - 1; i > 0; i--) {
        let j = Math.floor(Math.random() * (i + 1));
        [images[i], images[j]] = [images[j], images[i]];
      }
    }
  }

  // Map over this.state.cards and render a Card component for each card object
  render() {
    return (
      <div className="App">
        <header className="header">
          <h1 className="title">The Clicky Game</h1>
          <p className="rule">
            Try to click each image only once!
          </p>
        </header>
        <Score total={this.state.score}
               goal={8}
               status={this.state.status}
               />
        <Wrapper>
          {this.state.images.map(puppy => (
            <Card
              shuffleImg={this.shuffleImg}
              id={puppy.id}
              key={puppy.id}
              image={puppy.image}
            />
          ))}
        </Wrapper>
        <footer>
          <p>.</p>
        </footer>
    </div>
    );
  }
}

export default App;
